describe("POST /reklamationen", () => {
    test("Erstellt eine neue Reklamation", async () => {
      const neueReklamation = {
        produktId: 123,
        kunde: { name: "Anna Müller", email: "anna.mueller@example.com" },
        datum: "2024-06-15",
        beschreibung: "Das Produkt ist defekt"
      };
  
      const res = await request(app)
        .post("/reklamationen")
        .send(neueReklamation);
  
      expect(res.statusCode).toBe(201);
      expect(res.body).toHaveProperty("id");
      expect(res.body.status).toBe("Open");
    });
  
    test("Fehlende Felder führen zu Fehler", async () => {
      const falscheReklamation = { produktId: 123 };
  
      const res = await request(app)
        .post("/reklamationen")
        .send(falscheReklamation);
  
      expect(res.statusCode).toBe(400);
      expect(res.body).toHaveProperty("fehler");
    });
  });
  